import React from 'react';
import {AppContext} from '../App/AppProvider';
export default function (props) {
    return <AppContext.Consumer>
        {({coinList}) => {
            if (!coinList) {
                return <div> Loading Coins</div>
            }
            return <div> {props.children}</div>
        }
    }
    </AppContext.Consumer>
}
